import React, { Component } from 'react';
import { isMobile } from "react-device-detect";
import { Jumbotron, ListGroup, Card } from 'react-bootstrap';
import TDirectLogo from '../images/treasury-direct.png';
import '../styles/index.css'

class HomePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            windowWidth: window.outerWindow,
            windowHeight: window.outerHeight
        };

        this.handleResize = this.handleResize.bind(this)
        this.setClass = this.setClass.bind(this)
    }

    componentDidMount() {
        window.addEventListener("resize", this.handleResize);
    }

    handleResize() {
        setTimeout(() => {
            this.setState({ windowWidth: window.outerWidth, windowHeight: window.outerHeight })
        }, 100);
    }

    setClass(name) {
        const { windowHeight, windowWidth } = this.state

        if (isMobile && ((windowHeight > windowWidth) || (windowHeight < 500) || (windowWidth < 1024))) {
            return name + '-mobile'
        }
        else if (isMobile && ((windowHeight >= 1024) || (windowWidth >= 1024))) {
            return name + '-tablet'
        }
        else {
            return name
        }
    }

    render() {
        return (
            <React.Fragment>
                <Jumbotron className='roboto' style={{ color: '#343a40' }}>
                    <h1 style={{ fontSize: '2rem' }}>Quickly and easily price your savings bonds!</h1>
                    <h1 style={{ fontSize: '1rem' }}>VALUE, AGGREGATE, and EXPORT any set of I, E, EE Series Bonds or Savings Notes.</h1>
                    <div style={{ marginTop: '3rem' }}>
                        <p style={{ marginBottom: '.5rem' }}>Choose an option:</p>
                        <ListGroup style={{ width: '25rem' }}>
                            <ListGroup.Item><a style={{ color: '#ff420e', fontWeight: '500' }} href="/bond_pricing_individual">Price bonds individually</a></ListGroup.Item>
                            <ListGroup.Item><a style={{ color: '#ff420e', fontWeight: '500' }} href="/bond_pricing_spreadsheet">Price bonds from spreadsheet</a></ListGroup.Item>
                        </ListGroup>
                    </div>
                </Jumbotron>
                <div className={this.setClass('home-container')} style={{ width: '100%' }}>
                    <Card className={this.setClass('home-card')}>
                        <Card.Header className={'home-card-header'}>HOW WE DO IT</Card.Header>
                        <Card.Body className={'home-card-body'}>
                            <p>The <a target="_blank" rel="noopener noreferrer" href="https://www.treasurydirect.gov"><img style={{ width: '7.5rem', marginRight: '.5rem' }} src={TDirectLogo} alt="Treasury Direct" /></a>
                        provides historical <a href="https://www.treasurydirect.gov/indiv/tools/tools_savingsbondvalues_historical.htm">value files</a> which contain pricing data for all issued I, E/EE Series bonds and/or Savings Notes.
                        We use a database built from these source files to evaluate the price of your bond(s).
                        </p>
                            <ul>
                                <li>100% accurate results</li>
                                <li>View pricing data between May 1992 and May 2020</li>
                            </ul>
                        </Card.Body>
                    </Card>
                    <Card className={this.setClass('home-card')}>
                        <Card.Header className={'home-card-header'}>WHAT YOU'LL NEED</Card.Header>
                        <Card.Body className={'home-card-body'}>
                            <p style={{ marginBottom: '.25rem' }}>On your bond, you'll need to locate the following items:</p>
                            <div style={{ float: 'left', width: '50%' }}>
                                <p style={{ float: 'left', marginTop: '1rem', fontWeight: '500' }}>MANDATORY</p>
                                <ListGroup style={{ marginTop: '1rem' }}>
                                    <ListGroup.Item className={'home-list-item'} >&#8250; Denomination</ListGroup.Item>
                                    <ListGroup.Item className={'home-list-item'} >&#8250; Issue Date</ListGroup.Item>
                                    <ListGroup.Item className={'home-list-item'} >&#8250; Series</ListGroup.Item>
                                </ListGroup>
                            </div>
                            <div style={{ float: 'left', width: '50%' }}>
                                <p style={{ float: 'left', marginTop: '1rem', fontWeight: '500' }}>OPTIONAL</p>
                                <ListGroup style={{ marginTop: '1rem' }}>
                                    <ListGroup.Item className={'home-list-item'} >&#8250; Serial Number</ListGroup.Item>
                                </ListGroup>
                            </div>
                        </Card.Body>
                    </Card>
                    <Card className={this.setClass('home-card')}>
                        <Card.Header className={'home-card-header'}>FOR ASSISTANCE</Card.Header>
                        <Card.Body className={'home-card-body'}>
                            <p style={{ marginBottom: '.5rem' }}><a href="./instructions">Build a spreadsheet to import</a></p>
                            <p style={{ marginBottom: '.5rem' }}><a href="./instructions">Locate mandatory components on your bond</a></p>
                        </Card.Body>
                    </Card>
                </div>
            </React.Fragment >
        )
    }
}

export default HomePage;
