import React, { Component } from "react";
import { Form, Button, Table, Card } from "react-bootstrap";
import { CSVLink } from "react-csv";


class BondPricingTwo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            bonds: [],
            label: 'Attach .csv',
            totalPrice: 0,
            totalInterest: 0,
            totalValue: 0,
            totalDenomination: 0,
            totalDate: null,
            exportResult: []
        };

        this.setFilename = this.setFilename.bind(this);
        this.onSubmit = this.onSubmit.bind(this)
    }

    setFilename(e) {
        if (e.target.files.length > 0) {
            this.setState({ label: e.target.files[0].name })
        }
    }

    onSubmit(e) {
        e.preventDefault();

        if (this.state.hideTable) {
            this.setState({ hideTable: false, showCards: { visibility: 'visible' } })
        }

        var reader = new FileReader();

        reader.onload = () => {
            const csv = reader.result

            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' },
                body: JSON.stringify({ csv })
            };

            fetch('http://localhost:5000/update', requestOptions)
                .then(response => response.json())
                .then(response => {
                    let exports = []

                    exports.push(['Lookup Date', 'Serial Number', 'Series', 'Bond Amount', 'Issue Date', 'Next Accrual', 'Final Maturity', 'Issue Price', 'Interest', 'Interest Rate', 'Bond Value'])

                    for (let i = 0; i < response[0].length; i++) {
                        let values = [
                            response['vDate'],
                            response['serialNumber'],
                            response['series'],
                            '$' + response['denomination'],
                            response['iDate'],
                            response['aDate'],
                            response['mDate'],
                            '$' + response['iPrice'],
                            '$' + response['interest'],
                            '$' + response['value']
                        ]

                        exports.push(values);
                    }

                    this.setState({
                        totalDate: this.formatDate(response[1]['totalDate']),
                        totalDenomination: response[1]['totalDenominations'],
                        totalPrice: response[1]['totalPrices'],
                        totalInterest: response[1]['totalInterests'],
                        totalValue: response[1]['totalValues']
                    })

                    exports.push([
                        "",
                        "",
                        "",
                        '$' + response[1]['totalDenominations'],
                        "",
                        "",
                        "",
                        '$' + parseFloat(response[1]['totalPrices']).toFixed(2),
                        '$' + parseFloat(response[1]['totalInterests']).toFixed(2),
                        '$' + parseFloat(response[1]['totalValues']).toFixed(2)
                    ])

                    this.setState({ bonds: response[0], exportResults: exports })
                })
        }

        reader.readAsText(e.target.files[0]);
    }


    render() {
        return (
            <React.Fragment>
                <Form className="form-csv" style={{ position: 'relative' }}>
                    <Form.Label>ATTACHMENT</Form.Label>
                    <Form.File
                        id="custom-file"
                        label={this.state.label}
                        onChange={this.setFilename}
                        custom
                    />
                    <Button type="submit" size='sm' style={{ position: 'absolute', top: '0', right: '.5em', fontSize: '.65em', fontWeight: 'bold' }}>SUBMIT</Button>
                </Form>
                <div style={{ float: 'left', width: '100%', marginTop: '2em' }}>
                    {this.state.totals.length > 0 ?
                        <div style={{ width: '50em', margin: 'auto' }}>
                            <Card style={{ width: '15em', float: 'left' }}>
                                <Card.Body style={{ margin: 'auto' }} >
                                    <Card.Subtitle className="mb-2 text-muted" style={{ fontSize: '14px', fontWeight: 'bold', textAlign: 'center' }}>VALUE TOTAL</Card.Subtitle>
                                    <Card.Title style={{ color: 'green', fontSize: '28px', textAlign: 'center', marginBottom: '0' }}>{this.state.totals[0]}</Card.Title>
                                </Card.Body>
                            </Card>
                            <Card style={{ width: '15em', float: 'left', marginLeft: '2.5em' }}>
                                <Card.Body style={{ margin: 'auto' }}>
                                    <Card.Subtitle className="mb-2 text-muted" style={{ fontSize: '14px', fontWeight: 'bold', textAlign: 'center' }}>AMOUNT TOTAL</Card.Subtitle>
                                    <Card.Title style={{ fontSize: '28px', textAlign: 'center', marginBottom: '0' }}>{this.state.totals[1]}</Card.Title>
                                </Card.Body>
                            </Card>
                            <Card style={{ width: '15em', float: 'left', marginLeft: '2.5em' }}>
                                <Card.Body style={{ margin: 'auto' }}>
                                    <Card.Subtitle className="mb-2 text-muted" style={{ fontSize: '14px', fontWeight: 'bold', textAlign: 'center' }}>ISSUE PRICE TOTAL</Card.Subtitle>
                                    <Card.Title style={{ fontSize: '28px', textAlign: 'center', marginBottom: '0' }}>{this.state.totals[2]}</Card.Title>
                                </Card.Body>
                            </Card>
                        </div> : null}
                </div>
                <div style={{ float: 'left', width: '100%', margin: '.5em 0 .25em 0' }}>
                    <div style={{ width: '85%', margin: 'auto', textAlign: 'right' }}>
                        {this.state.rows.length > 0 ?
                            <CSVLink filename={'export_' + this.state.label} data={this.state.export}>+ [Export]</CSVLink> : null}
                    </div>
                </div>
                <div style={{ float: 'left', width: '100%' }}>
                    {
                        this.state.rows.length > 0 ?
                            <Table striped bordered hover style={{ width: '85%', margin: 'auto' }}>
                                <thead style={{ textAlign: "center", fontSize: "11px", textTransform: "uppercase" }}>
                                    <tr>
                                        <th>Lookup Date</th>
                                        <th>Serial Number</th>
                                        <th>Bond Type</th>
                                        <th>Bond Amount</th>
                                        <th>Issue Date</th>
                                        <th>Next Accrual</th>
                                        <th>Final Maturity</th>
                                        <th>Issue Price</th>
                                        <th>Interest</th>
                                        <th>Interest Rate</th>
                                        <th>Value</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.bonds.map((result) => (
                                        <tr key={result[1]}>
                                            <td>{result[0]}</td>
                                            <td>{result[1]}</td>
                                            <td>{result[2]}</td>
                                            <td>{result[3]}</td>
                                            <td>{result[4]}</td>
                                            <td>{result[5]}</td>
                                            <td>{result[6]}</td>
                                            <td>{result[7]}</td>
                                            <td>{result[8]}</td>
                                            <td>{result[9]}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </Table> : null}
                </div>
            </React.Fragment >
        )
    }
}

export default BondPricingTwo;
