import React, { Component } from "react";
import { Navbar } from "../../node_modules/react-bootstrap";
import { Form } from "../../node_modules/react-bootstrap";
import { Table } from "../../node_modules/react-bootstrap";

class Bonds extends Component {
    constructor(props) {
        super(props);
        this.state = {
            label: 'Choose file',
            results: []
        };

        this.setFilename = this.setFilename.bind(this);
    }

    setFilename(e) {
        this.setState({ label: e.target.files[0].name })

        var reader = new FileReader();

        reader.onload = () => {
            const csv = reader.result

            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' },
                body: JSON.stringify({ csv })
            };

            fetch('http://localhost:5000/upload', requestOptions)
                .then(response => response.json())
                .then(response => {
                    this.setState({ results: response })
                })
        }

        reader.readAsText(e.target.files[0]);
    }

    render() {
        return (
            <React.Fragment>
                <Navbar bg="dark" variant="dark">
                    <Navbar.Brand>Bond Pricing</Navbar.Brand>
                </Navbar>
                <Form className="form-csv">
                    <Form.File
                        id="custom-file"
                        label={this.state.label}
                        onChange={this.setFilename}
                        custom
                    />
                </Form>
                {this.state.results.length > 0 ?
                    <Table striped bordered hover size="sm" style={{ width: '80%', margin: 'auto', marginTop: '2.5%' }}>
                        <thead style={{ textAlign: "center", fontSize: "12px" }}>
                            <tr>
                                <th>Lookup Date</th>
                                <th>Serial Number</th>
                                <th>Bond Type</th>
                                <th>Bond Amount</th>
                                <th>Issue Date</th>
                                <th>Next Accrual</th>
                                <th>Final Maturity</th>
                                <th>Issue Price</th>
                                <th>Interest</th>
                                <th>Interest Rate</th>
                                <th>Value</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.results.map((result) => (
                                <tr key={result[1]}>
                                    <td>{result[0]}</td>
                                    <td>{result[1]}</td>
                                    <td>{result[2]}</td>
                                    <td>{result[3]}</td>
                                    <td>{result[4]}</td>
                                    <td>{result[5]}</td>
                                    <td>{result[6]}</td>
                                    <td>{result[7]}</td>
                                    <td>{result[8]}</td>
                                    <td>{result[9]}</td>
                                    <td>{result[10]}</td>
                                </tr>
                            ))}
                        </tbody>
                    </Table> : null}
            </React.Fragment >
        )
    }
}

export default Bonds;
