import React, { Component } from "react";
import { Navbar } from "../../node_modules/react-bootstrap";
import { Form } from "../../node_modules/react-bootstrap";
import { Table } from "../../node_modules/react-bootstrap";
import { Card } from "../../node_modules/react-bootstrap";
import { Spinner } from "../../node_modules/react-bootstrap";


class Bonds extends Component {
    constructor(props) {
        super(props);
        this.state = {
            label: 'Choose file',
            rows: [],
            totals: [],
            spinner: false
        };

        this.setFilename = this.setFilename.bind(this);
    }

    setFilename(e) {
        this.setState({ label: e.target.files[0].name, rows: [], totals: [], spinner: false })

        var reader = new FileReader();

        reader.onload = () => {
            const csv = reader.result

            this.setState({})

            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' },
                body: JSON.stringify({ csv })
            };

            fetch('http://localhost:5000/upload', requestOptions)
                .then(response => response.json())
                .then(response => {
                    this.setState({ rows: response['results'], totals: response['totals'] })
                })
        }

        reader.readAsText(e.target.files[0]);
    }

    render() {
        return (
            <React.Fragment>
                <Navbar bg="dark" variant="dark">
                    <Navbar.Brand>BOND PRICING</Navbar.Brand>
                </Navbar>
                <Form className="form-csv" style={{ position: 'relative' }}>
                    {this.state.spinner ? <Spinner animation="border" role="status" variant="secondary" style={{ position: 'absolute', top: '.25em', left: '-2.5em' }}></Spinner> : null}
                    <Form.File
                        id="custom-file"
                        label={this.state.label}
                        onChange={this.setFilename}
                        custom
                    />
                </Form>
                {this.state.totals.length > 0 ?
                    <div style={{ width: '50em', margin: 'auto', marginTop: '2.5%' }}>
                        <Card style={{ width: '15em', float: 'left' }}>
                            <Card.Body style={{ margin: 'auto' }} >
                                <Card.Subtitle className="mb-2 text-muted" style={{ fontSize: '14px', fontWeight: 'bold', textAlign: 'center' }}>VALUE TOTAL</Card.Subtitle>
                                <Card.Title style={{ color: 'green', fontSize: '28px', textAlign: 'center', marginBottom: '.25em' }}>{this.state.totals[0]}</Card.Title>
                            </Card.Body>
                        </Card>
                        <Card style={{ width: '15em', float: 'left', marginLeft: '2.5em' }}>
                            <Card.Body style={{ margin: 'auto' }}>
                                <Card.Subtitle className="mb-2 text-muted" style={{ fontSize: '14px', fontWeight: 'bold', textAlign: 'center' }}>AMOUNT TOTAL</Card.Subtitle>
                                <Card.Title style={{ fontSize: '28px', textAlign: 'center', marginBottom: '.25em' }}>{this.state.totals[1]}</Card.Title>
                            </Card.Body>
                        </Card>
                        <Card style={{ width: '15em', float: 'left', marginLeft: '2.5em' }}>
                            <Card.Body style={{ margin: 'auto' }}>
                                <Card.Subtitle className="mb-2 text-muted" style={{ fontSize: '14px', fontWeight: 'bold', textAlign: 'center' }}>ISSUE PRICE TOTAL</Card.Subtitle>
                                <Card.Title style={{ fontSize: '28px', textAlign: 'center', marginBottom: '.25em' }}>{this.state.totals[2]}</Card.Title>
                            </Card.Body>
                        </Card>
                    </div> : null}
                {
                    this.state.rows.length > 0 ?
                        <Table striped bordered hover style={{ width: '85%', margin: 'auto', marginTop: '10em' }}>
                            <thead style={{ textAlign: "center", fontSize: "11px", textTransform: "uppercase" }}>
                                <tr>
                                    <th>Lookup Date</th>
                                    <th>Serial Number</th>
                                    <th>Bond Type</th>
                                    <th>Bond Amount</th>
                                    <th>Issue Date</th>
                                    <th>Next Accrual</th>
                                    <th>Final Maturity</th>
                                    <th>Issue Price</th>
                                    <th>Interest</th>
                                    <th>Interest Rate</th>
                                    <th>Value</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.state.rows.map((result) => (
                                    <tr key={result[1]}>
                                        <td>{result[0]}</td>
                                        <td>{result[1]}</td>
                                        <td>{result[2]}</td>
                                        <td>{result[3]}</td>
                                        <td>{result[4]}</td>
                                        <td>{result[5]}</td>
                                        <td>{result[6]}</td>
                                        <td>{result[7]}</td>
                                        <td>{result[8]}</td>
                                        <td>{result[9]}</td>
                                        <td>{result[10]}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table> : null
                }
            </React.Fragment >
        )
    }
}

export default Bonds;
