import React, { Component } from "react";
import { Navbar } from "../../node_modules/react-bootstrap";
import { Form } from "../../node_modules/react-bootstrap";
import ReactFileReader from '../../node_modules/react-file-reader';

class Bonds extends Component {
    constructor(props) {
        super(props);
        this.state = {
            label: 'Choose file'
        };

        this.setFilename = this.setFilename.bind(this);
    }

    setFilename(e) {
        this.setState({ label: e.target.files[0].name })

        const fileReader = new FileReader();

        fileReader.onload = e => {
            var reader = new FileReader();

            reader.onload = function (e) {
                alert(reader.result)
            }
        }
    }

    render() {
        return (
            <React.Fragment>
                <Navbar bg="dark" variant="dark">
                    <Navbar.Brand>Bond Pricing</Navbar.Brand>
                </Navbar>
                <Form className="form-csv">
                    <Form.File
                        id="custom-file"
                        label={this.state.label}
                        onChange={this.setFilename}
                        custom
                    />
                </Form>
                <ReactFileReader handleFiles={this.setFilename} fileTypes={'.csv'}>
                    <button className='btn'>Upload</button>
                </ReactFileReader>
            </React.Fragment>
        )
    }
}

export default Bonds;
