import React, { Component } from 'react';
import { Table } from 'react-bootstrap';
//import bond from '../images/bond.png';
import excel from '../images/excel.png';

class Instructions extends Component {
    state = {}

    render() {
        const titles = ['Value as of Month/Year', 'Series', 'Denomination', 'Serial Number', 'Issue Month/Year']
        const descriptions = ['End date of which to value your bonds', 'Bond Type (ex. E, EE, I, Savings Note)', 'Purchase price of your bond', 'Unique identifier of your bond', 'Issue date of when the bond was purchased']

        return <div style={{ width: '75%', margin: 'auto', marginTop: '2em' }}>
            <ol>
                <li>First, youâ€™ll need a .csv file containing a list of your bonds with the following specifications:</li>
                <Table>
                    <tr>
                        {this.state.titles.map((title) => (
                            <td key={title}>{title}</td>
                        ))}
                    </tr>
                </Table>
                <img src={excel} alt="spreadsheet example" style={{ height: '75%', width: '75%', margin: '2em 0 2em 0' }}></img>
                <li>Once complete, navigate to the <a href="/valuations" style={{ fontSize: '.95em', fontWeight: '500' }}>VALUATIONS</a> page where you'll attach this csv to value your bonds.</li>
            </ol>
        </div>;
    }
}

export default Instructions;
