import React, { Component } from 'react';
import { Jumbotron, ListGroup } from 'react-bootstrap';
import '../index.css'

class HomePage extends Component {
    state = {}
    render() {
        return (
            <Jumbotron className='font-test' style={{ color: '#343a40' }}>
                <h1>Quickly and easily price your savings bonds!</h1>
                <h7>Evaluate, Aggregate, and Export any set of I, E, EE Series bonds or Savings Notes.</h7>
                <div style={{ width: '40%' }}>
                    <div style={{ margin: 'auto', marginTop: '2em', width: '50%' }}>
                        <p>Choose an option:</p>
                        <ListGroup>
                            <ListGroup.Item><a href="/bond_pricing_individual">Price bonds individually</a></ListGroup.Item>
                            <ListGroup.Item><a href="/bond_pricing_spreadsheet">Price bonds from spreadsheet</a></ListGroup.Item>
                        </ListGroup>
                    </div>
                </div>
            </Jumbotron >
        )
    }
}

export default HomePage;
