import React, { Component } from 'react';
import { Navbar, Nav } from "../../node_modules/react-bootstrap";
import Valuation from "./valuation";
import Instruction from "./instruction";

class Navigation extends Component {
    constructor(props) {
        super(props);
        this.state = {
            page: null
        };

        this.navOnClick = this.navOnClick.bind(this);
    }

    navOnClick(e) {
        let element = e.target.text

        if (element === 'INSTRUCTION') {
            this.setState({ page: <Instruction /> })
        }
        else if (element === 'VALUATION') {
            this.setState({ page: <Valuation /> })
        }
    }


    render() {
        return (
            <React.Fragment>
                <Navbar bg="dark" variant="dark" onClick={this.navOnClick}>
                    <Navbar.Brand>BOND PRICING</Navbar.Brand>
                    <Nav.Link key="instruction" href="/instruction">INSTRUCTION</Nav.Link>
                    <Nav.Link key="valuation" href="/valuation">VALUATION</Nav.Link>
                </Navbar>
                {this.state.page}
            </React.Fragment>
        )
    }
}

export default Navigation;
