import React, { Component } from 'react';
import { Table } from 'react-bootstrap';
//import bond from '../images/bond.png';
import excel from '../images/excel.png';

class Instructions extends Component {
    state = {}

    render() {
        const descriptions = [
            ['Value as of Month/Year', 'End date of which to value your bonds'],
            ['Series', 'Bond Type (ex. E, EE, I, Savings Note)'],
            ['Denomination', 'Purchase price of your bond'],
            ['Serial Number', 'Unique identifier of your bond'],
            ['Issue Month/Year', 'Issue date of when the bond was purchased']
        ]

        return <div style={{ width: '75%', margin: 'auto', marginTop: '2em' }}>
            <ol>
                <li>First, youâ€™ll need a .csv file containing a list of your bonds with the following specifications:</li>
                <Table style={{ marginTop: '1em' }}>
                    {descriptions.map((description) => (
                        <tr>
                            <td key={description[0]} style={{ border: 'none', fontWeight: 'bold', width: '25%', padding: '.25em', textTransform: 'uppercase', fontSize: '.75em' }}>{description[0]}</td>
                            <td key={description[1]} style={{ border: 'none', padding: '.25em' }}>{description[1]}</td>
                        </tr>
                    ))}
                </Table>
                <img src={excel} alt="spreadsheet example" style={{ height: '75%', width: '75%', margin: '2em 0 2em 0' }}></img>
                <li>Once complete, navigate to the <a href="/valuations" style={{ fontSize: '.95em', fontWeight: '500' }}>VALUATIONS</a> page where you'll attach this csv to value your bonds.</li>
            </ol>
        </div>;
    }
}

export default Instructions;
