import React, { Component } from 'react';
import { Form, Col, InputGroup, Button, OverlayTrigger, Tooltip, Table, Card } from 'react-bootstrap';
import '../index.css'

class BondPricingOne extends Component {
    constructor(props) {
        super(props);
        this.state = {
            currentBond: null,
            totalPrice: null,
            totalInterest: null,
            totalValue: null,
            totalDate: null,
            month: this.getCurrentMonth(),
            year: this.getCurrentYear(),
            showTooltip: { visibility: 'visible' },
            showCards: { visibility: 'hidden' },
            hideTable: true
        };

        this.onUpdateEnter = this.onUpdateEnter.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
        this.formatDate = this.formatDate.bind(this)
        this.appendToTable = this.appendToTable.bind(this)
    }

    formatDate(value_date) {
        const months = { "01": "Jan", "02": "Feb", "03": "Mar", "04": "Apr", "05": "May", "06": "June", "07": "Jul", "08": "Aug", "09": "Sept", "10": "Oct", "11": "Nov", "12": "Dec" }

        let month = value_date.split('/')[0]
        let year = value_date.split('/')[1]

        let month_string = months[month] + ' 1, ' + year

        return month_string
    }

    getCurrentMonth() {
        let d = new Date();
        return d.getMonth() + 1
    }

    getCurrentYear() {
        let d = new Date();

        return d.getFullYear()
    }

    appendToTable(currentBond) {
        function handleClickX(element) {
            element.parentNode.parentNode.remove()

            let rows = document.getElementById('bond-table').getElementsByTagName('tr')

            if (rows.length === 2) {
                element.parentNode.parentNode.remove()

                document.getElementById('bond-table').getElementsByTagName('tr')[0].style.visibility = 'hidden'
            }

            let total_prices = []
            let total_interests = []
            let total_values = []

            let prices = document.getElementsByClassName('issue-price')
            for (let i in prices) {
                let price = prices[i].innerHTML
                if (price !== undefined) {
                    total_prices.push(parseFloat(price.substring(1)))
                }
            }

            let interests = document.getElementsByClassName('bond-interest')
            for (let i in interests) {
                let interest = interests[i].innerHTML
                if (interest !== undefined) {
                    total_interests.push(parseFloat(interest.substring(1)))
                }
            }

            let values = document.getElementsByClassName('bond-value')

            for (let i in values) {
                let value = values[i].innerHTML
                if (value !== undefined) {
                    total_values.push(parseFloat(value.substring(1)))
                }
            }

            document.getElementById('card-price').innerHTML = '$' + parseFloat(total_prices.reduce((a, b) => a + b, 0)).toFixed(2)
            document.getElementById('card-interest').innerHTML = '$' + parseFloat(total_interests.reduce((a, b) => a + b, 0)).toFixed(2)
            document.getElementById('card-value').innerHTML = '$' + parseFloat(total_values.reduce((a, b) => a + b, 0)).toFixed(2)
        }

        const bondTable = document.getElementById('bond-table')

        bondTable.getElementsByTagName('tr')[0].style.visibility = 'visible'

        let bondRow = bondTable.insertRow(1);
        bondRow.style.textAlign = 'center'

        bondRow.insertCell(0).innerHTML = currentBond['serialNumber']
        bondRow.insertCell(1).innerHTML = currentBond['series']
        bondRow.insertCell(2).innerHTML = currentBond['denomination']
        bondRow.insertCell(3).innerHTML = currentBond['iDate']
        bondRow.insertCell(4).innerHTML = currentBond['aDate']
        bondRow.insertCell(5).innerHTML = currentBond['mDate']

        let cell6 = bondRow.insertCell(6)
        cell6.className = 'issue-price'
        cell6.innerHTML = '$' + currentBond['iPrice']

        let cell7 = bondRow.insertCell(7)
        cell7.className = 'bond-interest'
        cell7.innerHTML = '$' + currentBond['interest']

        let cell8 = bondRow.insertCell(8)
        cell8.className = 'bond-value'
        cell8.innerHTML = '$' + currentBond['value']
        cell8.style.position = 'relative'

        let span = document.createElement('span')
        span.innerHTML = '&times;'
        span.setAttribute('aria-hidden', 'true')
        span.style.border = 'none';

        let button = document.createElement('button')
        button.onclick = function () { handleClickX(this); };
        button.className = 'close'
        button.style.position = 'absolute'
        button.style.right = '-1em'

        button.append(span);
        cell8.append(button);

        let rows = bondTable.getElementsByTagName('tr')

        let total_prices = []
        let total_interests = []
        let total_values = []

        for (let i in rows) {
            if (!isNaN(i)) {
                if (i > 0 && i < (rows.length - 1)) {

                    let prices = rows[i].getElementsByClassName('issue-price')
                    for (let j in prices) {
                        let price = prices[j].innerHTML
                        if (price !== undefined) {
                            total_prices.push(parseFloat(price.substring(1)))
                        }
                    }

                    let interests = rows[i].getElementsByClassName('bond-interest')
                    for (let j in interests) {
                        let interest = interests[j].innerHTML
                        if (interest !== undefined) {
                            total_interests.push(parseFloat(interest.substring(1)))
                        }
                    }

                    let values = rows[i].getElementsByClassName('bond-value')
                    for (let j in values) {
                        let value = values[j].innerHTML
                        if (value !== undefined) {
                            total_values.push(parseFloat(value.substring(1)))
                        }
                    }
                }
            }
        }

        document.getElementById('card-price').innerHTML = parseFloat(total_prices.reduce((a, b) => a + b, 0)).toFixed(2)
        document.getElementById('card-interest').innerHTML = parseFloat(total_interests.reduce((a, b) => a + b, 0)).toFixed(2)
        document.getElementById('card-value').innerHTML = parseFloat(total_values.reduce((a, b) => a + b, 0)).toFixed(2)
    }

    onUpdateEnter(e) {
        e.preventDefault();

        setTimeout(() => {
            this.setState({ showTooltip: { visibility: 'hidden' } })
        }, 3500)
    }

    onSubmit(e) {
        e.preventDefault();

        if (this.state.hideTable) {
            this.setState({ hideTable: false, showCards: 'visible' })
        }

        const series_map = {
            'EE Bond': 'EE',
            'E Bond': 'E',
            'I Bond': 'I',
            'Savings Bond': 'S'
        }

        let bond = {
            'vMonth': e.target.aMonth.value,
            'vYear': e.target.aYear.value,
            'series': series_map[e.target.series.value],
            'denomination': (e.target.denomination.value).substring(1),
            'serialNumber': e.target.serialnumber.value,
            'iMonth': e.target.iMonth.value,
            'iYear': e.target.iYear.value
        }

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' },
            body: JSON.stringify({ bond })
        };

        fetch('http://localhost:5000/individual/bonds', requestOptions)
            .then(response => response.json())
            .then(response => {
                this.appendToTable(response)

                this.setState({
                    currentBond: response
                })
            })
    }

    render() {
        return (
            <div style={{ width: '75%', margin: 'auto', marginTop: '1.5em' }}>
                <Form onSubmit={this.onSubmit} style={{ width: '100%', fontSize: '14px' }}>
                    <Form.Row>
                        <Col className="col-padding" style={{ position: 'relative' }}>
                            <Form.Label>VALUE AS OF</Form.Label>
                            <InputGroup>
                                <Form.Control
                                    required
                                    name="aMonth"
                                    type="number"
                                    placeholder="Month"
                                    min="1"
                                    max="12"
                                    defaultValue={this.state.month}
                                />
                                <InputGroup.Append>
                                    <InputGroup.Text id="inputGroupPrepend">/</InputGroup.Text>
                                </InputGroup.Append>
                                <Form.Control
                                    required
                                    name="aYear"
                                    type="number"
                                    placeholder="Year"
                                    maxLength="4"
                                    min="1992"
                                    max={this.state.year}
                                    style={{ marginLeft: '-1px' }}
                                    defaultValue={this.state.year}
                                />
                            </InputGroup>
                            <OverlayTrigger placement='right' overlay={
                                <Tooltip style={this.state.showTooltip} >
                                    Revalue all bonds by this date.
                                    </Tooltip>}>
                                <Button type="button" size='sm' onMouseEnter={this.onUpdateEnter} variant='success' style={{ position: 'absolute', top: '0', right: '1.5em', fontSize: '.65em', fontWeight: 'bold' }}>UPDATE</Button>
                            </OverlayTrigger>
                        </Col>
                        <Col className="col-padding">
                            <Form.Label>SERIES</Form.Label>
                            <Form.Control name="series" as="select" custom>
                                <option>EE Bond</option>
                                <option>I Bond</option>
                                <option>E Bond</option>
                                <option>Savings Note</option>
                            </Form.Control>
                        </Col>
                        <Col className="col-padding">
                            <Form.Label>DENOMINATION</Form.Label>
                            <Form.Control name="denomination" as="select" custom>
                                <option>$25</option>
                                <option>$50</option>
                                <option>$75</option>
                                <option>$100</option>
                                <option>$200</option>
                                <option>$500</option>
                                <option>$1,000</option>
                                <option>$5,000</option>
                                <option>$10,000</option>
                            </Form.Control>
                        </Col>
                        <Col className="col-padding">
                            <Form.Label size='sm'>SERIAL NUMBER</Form.Label>
                            <Form.Control
                                name="serialnumber"
                                type="text"
                                placeholder="Optional"
                            />
                        </Col>
                        <Col style={{ position: 'relative' }}>
                            <Form.Label>ISSUE DATE</Form.Label>
                            <InputGroup>
                                <Form.Control
                                    required
                                    name="iMonth"
                                    type="number"
                                    placeholder="Month"
                                    maxLength="2"
                                    min="1"
                                    max="12"
                                    defaultValue="11"
                                />
                                <InputGroup.Append>
                                    <InputGroup.Text id="inputGroupPrepend">/</InputGroup.Text>
                                </InputGroup.Append>
                                <Form.Control
                                    required
                                    name="iYear"
                                    type="number"
                                    placeholder="Year"
                                    maxLength="4"
                                    min="1940"
                                    max={this.state.year}
                                    style={{ marginLeft: '-1px' }}
                                    defaultValue="2005"
                                />
                            </InputGroup>
                            <Button type="submit" size='sm' style={{ position: 'absolute', top: '0', right: '.5em', fontSize: '.65em', fontWeight: 'bold' }}>SUBMIT</Button>
                        </Col>
                    </Form.Row>
                </Form >
                <div id="card-container" style={{ width: '100%', float: 'left', paddingTop: '2em' }}>
                    <div style={this.state.showCards}>
                        <Card style={{ width: '20.50%', float: 'left' }}>
                            <Card.Header style={{ fontSize: '14px', padding: '.50em', fontWeight: '500' }}>VALUED FROM</Card.Header>
                            <Card.Body style={{ textAlign: 'center' }}>
                                <Card.Title>{this.state.currentBond ? this.formatDate(this.state.currentBond['vDate']) : null}</Card.Title>
                            </Card.Body>
                        </Card>
                        <Card style={{ width: '20.50%', float: 'left', marginLeft: '6%' }}>
                            <Card.Header style={{ fontSize: '14px', padding: '.50em', fontWeight: '500' }}>TOTAL COST</Card.Header>
                            <Card.Body style={{ textAlign: 'center' }}>
                                <Card.Title id="card-price">$0.00</Card.Title>
                            </Card.Body>
                        </Card>
                        <Card style={{ width: '20.50%', float: 'left', marginLeft: '6%' }}>
                            <Card.Header style={{ fontSize: '14px', padding: '.50em', fontWeight: '500' }}>TOTAL INTEREST</Card.Header>
                            <Card.Body style={{ textAlign: 'center' }}>
                                <Card.Title id="card-interest">$0.00</Card.Title>
                            </Card.Body>
                        </Card>
                        <Card style={{ width: '20.50%', float: 'left', marginLeft: '6%' }}>
                            <Card.Header style={{ fontSize: '14px', padding: '.50em', fontWeight: '500' }}>TOTAL VALUE</Card.Header>
                            <Card.Body style={{ textAlign: 'center' }}>
                                <Card.Title id="card-value">$0.00</Card.Title>
                            </Card.Body>
                        </Card>
                    </div>
                </div>
                <div style={{ width: '100%', float: 'left', marginTop: '1em' }}>
                    <Table striped bordered hover id="bond-table" hidden={this.state.hideTable} onChange={this.state.changeTable} style={{ width: '100%', margin: 'auto', marginBottom: '2em' }}>
                        <thead style={{ textAlign: "center", fontSize: "11px", textTransform: "uppercase" }}>
                            <tr>
                                <th>Serial Number</th>
                                <th>Series</th>
                                <th>Denomination</th>
                                <th>Issue Date</th>
                                <th>Next Accrual</th>
                                <th>Final Maturity</th>
                                <th>Issue Price</th>
                                <th>Interest</th>
                                <th>Value</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr></tr>
                        </tbody>
                    </Table>
                </div>
            </div >
        )
    }
}

export default BondPricingOne;
