import React, { Component } from "react";
import { Form, Table, Card, Row, Col, InputGroup, Button, OverlayTrigger, Tooltip, Pagination } from "react-bootstrap";
import { CSVLink } from "react-csv";
import '../index.css'


class BondPricingTwo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            bonds: [],
            label: 'Attach .csv',
            totalPrice: 0,
            totalInterest: 0,
            totalValue: 0,
            totalDenomination: 0,
            totalDate: null,
            month: this.getCurrentMonth(),
            year: this.getCurrentYear(),
            hideTable: true,
            showCards: { visibility: 'hidden' },
            export: "Value as of, Serial #, Series, Denomination, Issue Date, Next Accrual, Final Maturity, Issue Price, Interest, Value",
            exportResults: [],
            bondCount: [0, 1],
            currentPage: 0,
            currentRows: 5
        };

        this.setFilename = this.setFilename.bind(this)
        this.formatDate = this.formatDate.bind(this)
        this.onUpdateEnter = this.onUpdateEnter.bind(this)
        this.onUpdateClick = this.onUpdateClick.bind(this)
        this.appendToTable = this.appendToTable.bind(this)
        this.exportData = this.exportData.bind(this)
        this.setCurrentPage = this.setCurrentPage.bind(this)
        this.setNextPage = this.setNextPage.bind(this)
        this.exportOnClick = this.exportOnClick.bind(this)
    }

    exportOnClick() {
        const bonds = this.state.bonds
        let bondExports = []

        for (let i = 0; i < bonds.length; i++) {

            let bondExport = [
                bonds[i]['vDate'],
                bonds[i]['serialNumber'],
                bonds[i]['series'],
                '$' + this.convertValues(bonds[i]['denomination']),
                bonds[i]['iDate'],
                bonds[i]['aDate'],
                bonds[i]['mDate'],
                '$' + this.convertValues(parseFloat(bonds[i]['iPrice']).toFixed(2)),
                '$' + this.convertValues(parseFloat(bonds[i]['interest']).toFixed(2)),
                '$' + this.convertValues(parseFloat(bonds[i]['value']).toFixed(2))
            ]

            bondExports.push(bondExport);
        }


        bondExports.push([
            "",
            "",
            "",
            '$' + this.state.totalDenomination,
            "",
            "",
            "",
            '$' + parseFloat(this.state.totalPrice).toFixed(2),
            '$' + parseFloat(this.state.totalInterest).toFixed(2),
            '$' + parseFloat(this.state.totalValue).toFixed(2)
        ])

        this.setState({ exportResults: bondExports })
    }

    setCurrentPage(i) {
        this.setState({ currentPage: i })
    }

    setNextPage(i) {
        this.setState({ currentPage: i + 1 })
    }

    formatDate(value_date) {
        const months = { "01": "Jan.", "02": "Feb.", "03": "March", "04": "April", "05": "May", "06": "June", "07": "July", "08": "Aug.", "09": "Sept", "10": "Oct", "11": "Nov", "12": "Dec" }

        let month = value_date.split('/')[0]

        if (month.length === 1) {
            month = '0' + month
        }

        let year = value_date.split('/')[1]

        let month_string = months[month] + ' 1, ' + year

        return month_string
    }

    getCurrentMonth() {
        let d = new Date();
        return d.getMonth() + 1
    }

    getCurrentYear() {
        let d = new Date();

        return d.getFullYear()
    }

    exportData() {
        let exports = (this.state.export).split(',')

        let export_data = []

        let i = 0
        while ((i + 10) <= exports.length) {
            export_data.push(exports.slice(i, i + 10))
            i += 10
        }

        export_data.push(["", "", "", '$' + this.state.totalDenomination, "", "", "", '$' + parseFloat(this.state.totalPrice).toFixed(2), '$' + parseFloat(this.state.totalInterest).toFixed(2), '$' + parseFloat(this.state.totalValue).toFixed(2)])

        this.setState({ exportResults: export_data })
    }

    handleClickX(element) {
        element.parentNode.parentNode.remove()

        if ((document.getElementById('bond-table').getElementsByClassName('bond-row')).length > 0) {
            this.onUpdateClick();
        }
        else {
            this.setState({ totalPrice: 0, totalInterest: 0, totalValue: 0, totalDenomination: 0 })
        }
    }

    onUpdateEnter(e) {
        e.preventDefault();

        setTimeout(() => {
            this.setState({ showTooltip: { visibility: 'hidden' } })
        }, 3500)
    }

    onUpdateClick() {
        let rows = document.getElementById('bond-table').getElementsByTagName('tr')

        let bonds = []

        let vMonth = document.getElementsByName('aMonth')[0].value
        let vYear = document.getElementsByName('aYear')[0].value

        for (let i = 1; i < rows.length; i++) {
            let series = rows[i].getElementsByTagName('td')[1].innerHTML
            let denomination = (rows[i].getElementsByTagName('td')[2].innerHTML).substring(1)
            let serialNumber = rows[i].getElementsByTagName('td')[0].innerHTML
            let iMonth = (rows[i].getElementsByTagName('td')[3].innerHTML).split('/')[0]
            let iYear = (rows[i].getElementsByTagName('td')[3].innerHTML).split('/')[1]

            let bond = {
                'vMonth': vMonth,
                'vYear': vYear,
                'series': series,
                'denomination': denomination,
                'serialNumber': serialNumber,
                'iMonth': iMonth,
                'iYear': iYear,
            }

            bonds.push(bond)
        }

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' },
            body: JSON.stringify({ bonds })
        };

        fetch('http://localhost:5000/update', requestOptions)
            .then(response => response.json())
            .then(response => {
                let bondRows = document.getElementById('bond-table').getElementsByTagName('tbody')[0]
                bondRows.remove()

                let exports = [["Value as of", "Serial #", "Series", "Denomination", "Issue Date", "Next Accrual", "Final Maturity", "Issue Price", "Interest", "Value"]]

                for (let i = 0; i < response[0].length; i++) {
                    this.appendToTable(response[0][i])

                    exports.push([
                        response[0][i]['vDate'],
                        response[0][i]['serialNumber'],
                        response[0][i]['series'],
                        '$' + response[0][i]['denomination'],
                        response[0][i]['iDate'],
                        '$' + parseFloat(response[0][i]['iPrice']).toFixed(2),
                        response[0][i]['aDate'],
                        response[0][i]['mDate'],
                        '$' + parseFloat(response[0][i]['value']).toFixed(2),
                        '$' + parseFloat(response[0][i]['interest']).toFixed(2)
                    ])
                }

                exports.push([
                    "",
                    "",
                    "",
                    '$' + response[1]['totalDenominations'],
                    "",
                    "",
                    "",
                    '$' + parseFloat(response[1]['totalPrices']).toFixed(2),
                    '$' + parseFloat(response[1]['totalInterests']).toFixed(2),
                    '$' + parseFloat(response[1]['totalValues']).toFixed(2)
                ])

                this.setState({
                    totalDate: this.formatDate(response[1]['totalDate']),
                    totalDenomination: response[1]['totalDenominations'],
                    totalPrice: response[1]['totalPrices'],
                    totalInterest: response[1]['totalInterests'],
                    totalValue: response[1]['totalValues']
                })

                this.setState({ exportResults: exports })
            })
    }

    appendToTable(currentBond) {
        const bondTable = document.getElementById('bond-table')

        let bondBody = null

        if (bondTable.getElementsByTagName('tbody').length === 0) {
            bondBody = document.createElement('tbody')
            bondTable.append(bondBody)
        }
        else {
            bondBody = bondTable.getElementsByTagName('tbody')[0]
        }

        if (currentBond['interest'] < 0) {
            currentBond['interest'] = 0
        }

        bondTable.getElementsByTagName('tr')[0].style.visibility = 'visible'

        let bondRow = bondBody.insertRow();
        bondRow.className = "bond-row"
        bondRow.style.textAlign = 'center'

        bondRow.insertCell(0).innerHTML = currentBond['serialNumber']
        bondRow.insertCell(1).innerHTML = currentBond['series']

        let cell2 = bondRow.insertCell(2)
        cell2.className = 'bond-denomination'
        cell2.innerHTML = '$' + this.convertValues(currentBond['denomination'])

        bondRow.insertCell(3).innerHTML = currentBond['iDate']
        bondRow.insertCell(4).innerHTML = currentBond['aDate']
        bondRow.insertCell(5).innerHTML = currentBond['mDate']

        let cell6 = bondRow.insertCell(6)
        cell6.className = 'bond-price'
        cell6.innerHTML = '$' + this.convertValues(parseFloat(currentBond['iPrice']).toFixed(2))

        let cell7 = bondRow.insertCell(7)
        cell7.className = 'bond-interest'
        cell7.innerHTML = '$' + this.convertValues(parseFloat(currentBond['interest']).toFixed(2))

        let cell8 = bondRow.insertCell(8)
        cell8.className = 'bond-value'
        cell8.innerHTML = '$' + this.convertValues(parseFloat(currentBond['value']).toFixed(2))
        cell8.style.position = 'relative'

        let span = document.createElement('span')
        span.innerHTML = '&times;'
        span.setAttribute('aria-hidden', 'true')
        span.style.border = 'none';

        let button = document.createElement('button')
        button.onclick = () => { this.handleClickX(button) };
        button.className = 'close'
        button.style.position = 'absolute'
        button.style.right = '-1em'

        button.append(span);
        cell8.append(button);

        this.exportData()
    }

    setFilename(e) {
        if (e.target.files.length > 0) {
            this.setState({ label: e.target.files[0].name })
        }

        if (this.state.hideTable) {
            this.setState({ hideTable: false, showCards: { visibility: 'visible' } })
        }

        let vMonth = document.getElementsByName('aMonth')[0].value
        let vYear = document.getElementsByName('aYear')[0].value

        var reader = new FileReader();

        reader.onload = () => {
            const bonds = reader.result

            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' },
                body: JSON.stringify({ bonds, vMonth, vYear })
            };

            fetch('http://localhost:5000/upload', requestOptions)
                .then(response => response.json())
                .then(response => {
                    let exports = []

                    exports.push(['Lookup Date', 'Serial Number', 'Series', 'Bond Amount', 'Issue Date', 'Next Accrual', 'Final Maturity', 'Issue Price', 'Interest', 'Bond Value'])

                    for (let i = 0; i < response[0].length; i++) {
                        let values = [
                            response[0][i]['vDate'],
                            response[0][i]['serialNumber'],
                            response[0][i]['series'],
                            '$' + response[0][i]['denomination'],
                            response[0][i]['iDate'],
                            '$' + parseFloat(response[0][i]['iPrice']).toFixed(2),
                            response[0][i]['aDate'],
                            response[0][i]['mDate'],
                            '$' + parseFloat(response[0][i]['value']).toFixed(2),
                            '$' + parseFloat(response[0][i]['interest']).toFixed(2)
                        ]

                        exports.push(values);
                    }

                    this.setState({
                        totalDate: this.formatDate(response[1]['totalDate']),
                        totalDenomination: response[1]['totalDenominations'],
                        totalPrice: response[1]['totalPrices'],
                        totalInterest: response[1]['totalInterests'],
                        totalValue: response[1]['totalValues']
                    })

                    exports.push([
                        "",
                        "",
                        "",
                        '$' + response[1]['totalDenominations'],
                        "",
                        "",
                        "",
                        '$' + parseFloat(response[1]['totalPrices']).toFixed(2),
                        '$' + parseFloat(response[1]['totalInterests']).toFixed(2),
                        '$' + parseFloat(response[1]['totalValues']).toFixed(2)
                    ])

                    this.setState({ bonds: response[0], exportResults: exports })
                })
        }

        reader.readAsText(e.target.files[0]);
    }

    convertValues(value) {
        return value.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")
    }

    render() {
        return (
            <div>
                <Form className="form-csv" style={{ position: 'relative' }}>
                    <Form.Row style={{ float: 'left', width: '30%' }}>
                        <Col>
                            <Form.Label>VALUE AS OF</Form.Label>
                            <InputGroup>
                                <Form.Control
                                    required
                                    name="aMonth"
                                    type="number"
                                    placeholder="Month"
                                    min="1"
                                    max="12"
                                    defaultValue={this.state.month}
                                />
                                <InputGroup.Append>
                                    <InputGroup.Text id="inputGroupPrepend">/</InputGroup.Text>
                                </InputGroup.Append>
                                <Form.Control
                                    required
                                    name="aYear"
                                    type="number"
                                    placeholder="Year"
                                    maxLength="4"
                                    min="1992"
                                    max={this.state.year}
                                    style={{ marginLeft: '-1px' }}
                                    defaultValue={this.state.year}
                                />
                            </InputGroup>
                            <OverlayTrigger placement='right' overlay={
                                <Tooltip style={this.state.showTooltip} >
                                    Revalue all bonds by this date.
                                    </Tooltip>}>
                                <Button type="button" size='sm' variant='success' onMouseEnter={this.onUpdateEnter} onClick={this.onUpdateClick} style={{ position: 'absolute', top: '0', right: '.5em', fontSize: '.65em', fontWeight: 'bold' }}>UPDATE</Button>
                            </OverlayTrigger>
                        </Col>
                    </Form.Row>
                    <Form.Row>
                        <Col style={{ marginLeft: '2em' }}>
                            <Form.Label>ATTACHMENT</Form.Label>
                            <Form.File
                                id="custom-file"
                                label={this.state.label}
                                onChange={this.setFilename}
                                custom
                            />
                        </Col>
                    </Form.Row>
                </Form>
                <div style={{ width: '70%', margin: 'auto' }}>
                    <div id="card-container" style={{ width: '100%', float: 'left', paddingTop: '1em' }}>
                        {this.state.exportResults.length > 0 ? <div onClick={this.exportOnClick} style={{ width: '100%', textAlign: 'right', marginBottom: '.25em' }}>
                            <CSVLink filename={'bonds_valued_from_' + this.state.month + '_' + this.state.year} data={this.state.exportResults.length > 0 ? this.state.exportResults : ''}>+ [Export]</CSVLink>
                        </div> : null}
                        <Row>
                            <Col>
                                <Card style={this.state.showCards}>
                                    <Card.Header className='card-header'>VALUED AS OF</Card.Header>
                                    <Card.Body style={{ textAlign: 'center' }}>
                                        <Card.Title>{this.state.totalDate}</Card.Title>
                                    </Card.Body>
                                </Card>
                            </Col>
                            <Col>
                                <Card style={this.state.showCards}>
                                    <Card.Header className='card-header'>TOTAL FACE VALUE</Card.Header>
                                    <Card.Body style={{ textAlign: 'center' }}>
                                        <Card.Title>${this.convertValues(this.state.totalDenomination)}</Card.Title>
                                    </Card.Body>
                                </Card>
                            </Col>
                            <Col>
                                <Card style={this.state.showCards}>
                                    <Card.Header className='card-header'>TOTAL COST</Card.Header>
                                    <Card.Body style={{ textAlign: 'center' }}>
                                        <Card.Title id="card-price" style={{ color: 'red' }}>${this.convertValues(parseFloat(this.state.totalPrice).toFixed(2))}</Card.Title>
                                    </Card.Body>
                                </Card>
                            </Col>
                            <Col>
                                <Card style={this.state.showCards}>
                                    <Card.Header className='card-header'>TOTAL INTEREST</Card.Header>
                                    <Card.Body style={{ textAlign: 'center' }}>
                                        <Card.Title id="card-interest" style={{ color: '#ff8c00' }}>${this.convertValues(parseFloat(this.state.totalInterest).toFixed(2))}</Card.Title>
                                    </Card.Body>
                                </Card>
                            </Col>
                            <Col>
                                <Card style={this.state.showCards}>
                                    <Card.Header className='card-header'>TOTAL VALUE</Card.Header>
                                    <Card.Body style={{ textAlign: 'center' }}>
                                        <Card.Title id="card-value" style={{ color: 'green' }}>${this.convertValues(parseFloat(this.state.totalValue).toFixed(2))}</Card.Title>
                                    </Card.Body>
                                </Card>
                            </Col>
                        </Row>
                    </div >
                    <div style={{ width: '100%', float: 'left', margin: '1em 0' }}>
                        {
                            this.state.bonds.length > 0 ?
                                <div>
                                    <Table striped bordered hover id="bond-table" hidden={this.state.hideTable} onChange={this.state.changeTable} style={{ width: '100%', margin: 'auto' }}>
                                        <thead style={{ textAlign: "center", fontSize: ".75em", textTransform: "uppercase" }}>
                                            <tr>
                                                <th>Serial Number</th>
                                                <th>Bond Type</th>
                                                <th>Bond Amount</th>
                                                <th>Issue Date</th>
                                                <th>Next Accrual</th>
                                                <th>Final Maturity</th>
                                                <th>Issue Price</th>
                                                <th>Interest</th>
                                                <th>Value</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {this.state.bonds
                                                .slice(this.state.currentPage * 5, (this.state.currentPage + 1) * 5)
                                                .map((bond, i) => (
                                                    <tr key={i}>
                                                        <td>{bond['serialNumber']}</td>
                                                        <td>{bond['series']}</td>
                                                        <td>${this.convertValues(bond['denomination'])}</td>
                                                        <td>{bond['iDate']}</td>
                                                        <td>{bond['aDate']}</td>
                                                        <td>{bond['mDate']}</td>
                                                        <td>${this.convertValues(parseFloat(bond['iPrice']).toFixed(2))}</td>
                                                        <td>${this.convertValues(parseFloat(bond['interest']).toFixed(2))}</td>
                                                        <td>${this.convertValues(parseFloat(bond['value']).toFixed(2))}</td>
                                                    </tr>
                                                ))}
                                        </tbody>
                                    </Table>
                                    {this.state.bonds.length > 0 ?
                                        <Pagination size='sm'>
                                            {this.state.bondCount.map((i) => (
                                                <React.Fragment>
                                                    <Pagination.Item active={i === this.state.currentPage} key={i} onClick={() => this.setCurrentPage(i)}>{i + 1}</Pagination.Item>
                                                </React.Fragment>
                                            ))}
                                        </Pagination> : null}
                                </div>
                                : null}
                    </div>
                </div>
            </div >
        )
    }
}

export default BondPricingTwo;
