import React, { Component } from 'react';
import { Jumbotron, ListGroup, Card } from 'react-bootstrap';
import TDirectLogo from '../images/treasury-direct.png';
import '../index.css'

class HomePage extends Component {
    state = {}
    render() {
        return (
            <React.Fragment>
                <Jumbotron className='font-test' style={{ color: '#343a40' }}>
                    <h1 style={{ fontSize: '2em' }}>Quickly and easily price your savings bonds!</h1>
                    <h1 style={{ fontSize: '1em' }}>Value, aggregate, and export any set of I, E, EE Series bonds or Savings Notes.</h1>
                    <div style={{ width: '40%' }}>
                        <div style={{ margin: 'auto', marginTop: '2em', width: '60%' }}>
                            <p>Choose an option:</p>
                            <ListGroup>
                                <ListGroup.Item><a href="/bond_pricing_individual">Price bonds individually</a></ListGroup.Item>
                                <ListGroup.Item><a href="/bond_pricing_spreadsheet">Price bonds from spreadsheet</a></ListGroup.Item>
                            </ListGroup>
                        </div>
                    </div>
                </Jumbotron >
                <Card style={{ width: '48%', marginLeft: '2%' }}>
                    <Card.Header style={{ background: 'white', borderBottom: 'none', fontSize: '1.25em', fontWeight: 'normal' }}>HOW WE DO IT</Card.Header>
                    <Card.Body>
                        <a href="https://www.treasurydirect.gov"><img style={{ width: '7.5em' }} src={TDirectLogo} alt="Treasury Direct" /></a>

                    </Card.Body>
                </Card>
            </React.Fragment>
        )
    }
}

export default HomePage;
