import React, { Component } from 'react';
//import { Table } from 'react-bootstrap';
import { CSVLink } from "react-csv";
//import excel from '../images/excel.png';

class Instructions extends Component {
    state = {}

    render() {
        const exportData = [['Serial Number', 'Series', 'Denomination', 'Issue Date']]

        return (
            <React.Fragment>
                <div className="jumbotron-pages"></div>
                <div style={{ width: '70%', margin: 'auto', marginTop: '2em' }}>
                    <CSVLink data={exportData} style={{ float: 'left', width: '100%', textAlign: 'right' }}>[Download CSV Template]</CSVLink>
                </div>
            </React.Fragment>
        )
    }
}

export default Instructions;
