import React, { Component } from 'react';
import { isMobile } from "react-device-detect";
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';
import { BrowserRouter, Switch, Route } from 'react-router-dom';
import HomePage from './index';
import Instruction from './instructions';
import BondPricingOne from './bond_pricing_individual';
import BondPricingTwo from './bond_pricing_spreadsheet';
//import BondExample from './bond_example';
import logo from '../images/bond-logo.png';
import '../index.css'

class Navigation extends Component {
    constructor(props) {
        super(props);
        this.state = {
            windowWidth: window.outerWidth
        };

        this.handleResize = this.handleResize.bind(this)
    }

    componentDidMount() {
        window.addEventListener("resize", this.handleResize);
    }

    handleResize() {
        setTimeout(() => {
            this.setState({ windowWidth: window.outerWidth })
            console.log(this.state.windowWidth)
        }, 100);
    }

    render() {
        const { windowWidth } = this.state

        return (
            <BrowserRouter style={{ width: '100%', maxWidth: windowWidth }}>
                <Navbar style={{ position: 'absolute' }}>
                    <Navbar.Brand style={{ marginRight: '.25rem' }}>
                        <img
                            src={logo}
                            width="35"
                            height="35"
                            alt="SAVINGS BOND PRICING TOOL"
                        /></Navbar.Brand>
                    <Nav.Link className={isMobile && windowWidth < 550 ? 'mobile-link' : null} href="/">HOME</Nav.Link>
                    <Nav.Link className={isMobile && windowWidth < 550 ? 'mobile-link' : null} href="/instructions">INSTRUCTIONS</Nav.Link>
                    {isMobile && windowWidth < 600 ?
                        <NavDropdown title="BOND PRICING" style={{ fontSize: '.75rem', fontWeight: 'bold' }} alignRight>
                            <NavDropdown.Item href="/bond_pricing_individual" style={{ fontSize: '.5rem', color: '#2a3132' }}>INDIVIDUAL</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item href="/bond_pricing_spreadsheet" style={{ fontSize: '.5rem', color: '#2a3132' }}>SPREADSHEET</NavDropdown.Item>
                        </NavDropdown> :
                        <NavDropdown title="BOND PRICING" style={{ fontSize: '.75rem', fontWeight: 'bold' }}>
                            <NavDropdown.Item href="/bond_pricing_individual" style={{ fontSize: '.75rem', color: '#2a3132' }}>INDIVIDUAL</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item href="/bond_pricing_spreadsheet" style={{ fontSize: '.75rem', color: '#2a3132' }}>SPREADSHEET</NavDropdown.Item>
                        </NavDropdown>}
                </Navbar>
                <Switch>
                    <Route exact path='/' component={HomePage} />
                    <Route exact path='/instructions' component={Instruction} />
                    <Route exact path='/bond_pricing_individual' component={BondPricingOne} />
                    <Route exact path='/bond_pricing_spreadsheet' component={BondPricingTwo} />
                </Switch>
            </BrowserRouter>
        )
    }
}

export default Navigation;
