import React, { Component } from 'react';
import { Form, Col, InputGroup, Button, OverlayTrigger, Tooltip, Table, Card } from 'react-bootstrap';
import { CSVLink } from "react-csv";
import '../index.css'

class BondPricingOne extends Component {
    constructor(props) {
        super(props);
        this.state = {
            totalPrice: 0,
            totalInterest: 0,
            totalValue: 0,
            example: 0,
            totalDenomination: 0,
            month: this.getCurrentMonth(),
            year: this.getCurrentYear(),
            showTooltip: { visibility: 'visible' },
            showCards: { visibility: 'hidden' },
            hideTable: true,
            export: "Value as of, Serial #, Series, Denomination, Issue Date, Next Accrual, Final Maturity, Issue Price, Interest, Value",
            exportResults: []
        };

        this.onUpdateEnter = this.onUpdateEnter.bind(this)
        this.onUpdateClick = this.onUpdateClick.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
        this.formatDate = this.formatDate.bind(this)
        this.appendToTable = this.appendToTable.bind(this)
        this.exportData = this.exportData.bind(this)
    }

    formatDate(value_date) {
        const months = { "01": "Jan.", "02": "Feb.", "03": "March", "04": "April", "05": "May", "06": "June", "07": "July", "08": "Aug.", "09": "Sept", "10": "Oct", "11": "Nov", "12": "Dec" }

        let month = value_date.split('/')[0]
        let year = value_date.split('/')[1]

        let month_string = months[month] + ' 1, ' + year

        return month_string
    }

    getCurrentMonth() {
        let d = new Date();
        return d.getMonth() + 1
    }

    getCurrentYear() {
        let d = new Date();

        return d.getFullYear()
    }

    handleClickX(element) {
        let row = element.parentNode.parentNode

        let bond_denomination = (row.getElementsByClassName('bond-denomination')[0].innerHTML).substring(1)
        let bond_price = (row.getElementsByClassName('bond-price')[0].innerHTML).substring(1)
        let bond_interest = (row.getElementsByClassName('bond-interest')[0].innerHTML).substring(1)
        let bond_value = row.getElementsByClassName('bond-value')[0].innerText
        bond_value = bond_value.substring(1, bond_value.length - 2)

        let new_total_denomination = "$" + parseFloat(parseFloat((this.state.totalDenomination).substring(1)) - bond_denomination).toFixed(2)
        let new_total_price = "$" + parseFloat(parseFloat((this.state.totalPrice).substring(1)) - bond_price).toFixed(2)
        let new_total_interest = "$" + parseFloat(parseFloat((this.state.totalInterest).substring(1)) - bond_interest).toFixed(2)
        let new_total_value = "$" + parseFloat(parseFloat((this.state.totalValue).substring(1)) - bond_value).toFixed(2)

        if (((this.state.totalValue).substring(1) - bond_value) === 0) {
            document.getElementById('bond-table').getElementsByTagName('tr')[0].style.visibility = 'hidden'
        }

        this.setState({ totalDenomination: new_total_denomination, totalPrice: new_total_price, totalInterest: new_total_interest, totalValue: new_total_value })

        element.parentNode.parentNode.remove()
    }

    appendToTable(currentBond) {
        const bondTable = document.getElementById('bond-table')

        let bondBody = null

        if (bondTable.getElementsByTagName('tbody').length === 0) {
            bondBody = document.createElement('tbody')
            bondTable.append(bondBody)
        }
        else {
            bondBody = bondTable.getElementsByTagName('tbody')[0]
        }

        bondTable.getElementsByTagName('tr')[0].style.visibility = 'visible'

        let bondRow = bondBody.insertRow();
        bondRow.className = "bond-row"
        bondRow.style.textAlign = 'center'

        bondRow.insertCell(0).innerHTML = currentBond['serialNumber']
        bondRow.insertCell(1).innerHTML = currentBond['series']

        let cell2 = bondRow.insertCell(2)
        cell2.className = 'bond-denomination'
        cell2.innerHTML = currentBond['denomination']

        bondRow.insertCell(3).innerHTML = currentBond['iDate']
        bondRow.insertCell(4).innerHTML = currentBond['aDate']
        bondRow.insertCell(5).innerHTML = currentBond['mDate']

        let cell6 = bondRow.insertCell(6)
        cell6.className = 'bond-price'
        cell6.innerHTML = '$' + currentBond['iPrice']

        let cell7 = bondRow.insertCell(7)
        cell7.className = 'bond-interest'
        cell7.innerHTML = '$' + currentBond['interest']

        let cell8 = bondRow.insertCell(8)
        cell8.className = 'bond-value'
        cell8.innerHTML = '$' + currentBond['value']
        cell8.style.position = 'relative'

        let span = document.createElement('span')
        span.innerHTML = '&times;'
        span.setAttribute('aria-hidden', 'true')
        span.style.border = 'none';

        let button = document.createElement('button')
        button.onclick = () => { this.handleClickX(button) };
        button.className = 'close'
        button.style.position = 'absolute'
        button.style.right = '-1em'

        button.append(span);
        cell8.append(button);

        /*let rows = bondTable.getElementsByClassName('bond-row')

        let total_denominations =[]
        let total_prices = []
        let total_interests = []
        let total_values = []

        for (let i = 0; i < rows.length; i++) {
            let prices = rows[i].getElementsByClassName('bond-price')
            for (let j in prices) {
                let price = prices[j].innerHTML
                if (price !== undefined) {
                    total_prices.push(parseFloat(price.substring(1)))
                }
            }

            let interests = rows[i].getElementsByClassName('bond-interest')
            for (let j in interests) {
                let interest = interests[j].innerHTML
                if (interest !== undefined) {
                    total_interests.push(parseFloat(interest.substring(1)))
                }
            }

            let values = rows[i].getElementsByClassName('bond-value')
            for (let j in values) {
                let value = values[j].innerHTML
                if (value !== undefined) {
                    total_values.push(parseFloat(value.substring(1)))
                }
            }
        }

        let total_price = '$' + parseFloat(total_prices.reduce((a, b) => a + b, 0)).toFixed(2)
        let total_interest = '$' + parseFloat(total_interests.reduce((a, b) => a + b, 0)).toFixed(2)
        let total_value = '$' + parseFloat(total_values.reduce((a, b) => a + b, 0)).toFixed(2)
        */

        //this.setState({ totalPrice: total_price, totalInterest: total_interest, totalValue: total_value })

        this.exportData()
    }

    onUpdateEnter(e) {
        e.preventDefault();

        setTimeout(() => {
            this.setState({ showTooltip: { visibility: 'hidden' } })
        }, 3500)
    }

    onUpdateClick() {
        let rows = document.getElementById('bond-table').getElementsByTagName('tr')

        let bonds = []

        let vMonth = document.getElementsByName('aMonth')[0].value
        let vYear = document.getElementsByName('aYear')[0].value

        for (let i = 1; i < rows.length; i++) {
            let series = rows[i].getElementsByTagName('td')[1].innerHTML
            let denomination = (rows[i].getElementsByTagName('td')[2].innerHTML).substring(1)
            let serialNumber = rows[i].getElementsByTagName('td')[0].innerHTML
            let iMonth = (rows[i].getElementsByTagName('td')[3].innerHTML).split('/')[0]
            let iYear = (rows[i].getElementsByTagName('td')[3].innerHTML).split('/')[1]

            let bond = {
                'vMonth': vMonth,
                'vYear': vYear,
                'series': series,
                'denomination': denomination,
                'serialNumber': serialNumber,
                'iMonth': iMonth,
                'iYear': iYear,
            }

            bonds.push(bond)
        }

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' },
            body: JSON.stringify({ bonds })
        };

        fetch('http://localhost:5000/update', requestOptions)
            .then(response => response.json())
            .then(response => {
                let bondRows = document.getElementById('bond-table').getElementsByTagName('tbody')[0]
                bondRows.remove()

                for (let i = 0; i < response.length; i++) {
                    this.appendToTable(response[i])
                }
            })
    }

    exportData() {
        let exports = (this.state.export).split(',')

        let export_data = []

        let i = 0
        while ((i + 10) <= exports.length) {
            export_data.push(exports.slice(i, i + 10))
            i += 10
        }

        console.log(this.state.totalPrice)

        export_data.push(["", "", "", "", "", "", "", this.state.totalPrice, this.state.totalInterest, this.state.totalValue])

        this.setState({ exportResults: export_data })
    }

    onSubmit(e) {
        e.preventDefault();

        if (this.state.hideTable) {
            this.setState({ hideTable: false, showCards: { visibility: 'visible' } })
        }

        const series_map = {
            'EE Bond': 'EE',
            'E Bond': 'E',
            'I Bond': 'I',
            'Savings Bond': 'S'
        }

        let bond = {
            'vMonth': e.target.aMonth.value,
            'vYear': e.target.aYear.value,
            'series': series_map[e.target.series.value],
            'denomination': (e.target.denomination.value).substring(1),
            'serialNumber': e.target.serialnumber.value,
            'iMonth': e.target.iMonth.value,
            'iYear': e.target.iYear.value
        }

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' },
            body: JSON.stringify({ bond })
        };

        fetch('http://localhost:5000/individual/bonds', requestOptions)
            .then(response => response.json())
            .then(response => {
                this.appendToTable(response)

                this.setState({ currentBond: response })

                let values = [
                    response['vDate'],
                    response['serialNumber'],
                    response['series'],
                    '$' + response['denomination'],
                    response['iDate'],
                    response['aDate'],
                    response['mDate'],
                    '$' + response['iPrice'],
                    '$' + response['interest'],
                    '$' + response['value']
                ]

                console.log(response['iPrice'])

                this.setState({
                    totalDenomination: '$' + (this.state.totalDenomination + response['denomination']),
                    totalPrice: '$' + (this.state.totalPrice + response['iPrice']),
                    totalInterest: '$' + (this.state.totalInterest + response['interest']),
                    totalValue: '$' + (this.state.totalValue + response['value'])
                })

                this.setState({ export: this.state.export.concat("," + values.toString()) })

                this.exportData()
            })
    }

    render() {
        return (
            <div style={{ width: '75%', margin: 'auto', marginTop: '1.5em' }}>
                <Form onSubmit={this.onSubmit} style={{ width: '100%', fontSize: '14px' }}>
                    <Form.Row>
                        <Col className="col-padding" style={{ position: 'relative' }}>
                            <Form.Label>VALUE AS OF</Form.Label>
                            <InputGroup>
                                <Form.Control
                                    required
                                    name="aMonth"
                                    type="number"
                                    placeholder="Month"
                                    min="1"
                                    max="12"
                                    defaultValue={this.state.month}
                                />
                                <InputGroup.Append>
                                    <InputGroup.Text id="inputGroupPrepend">/</InputGroup.Text>
                                </InputGroup.Append>
                                <Form.Control
                                    required
                                    name="aYear"
                                    type="number"
                                    placeholder="Year"
                                    maxLength="4"
                                    min="1992"
                                    max={this.state.year}
                                    style={{ marginLeft: '-1px' }}
                                    defaultValue={this.state.year}
                                />
                            </InputGroup>
                            <OverlayTrigger placement='right' overlay={
                                <Tooltip style={this.state.showTooltip} >
                                    Revalue all bonds by this date.
                                    </Tooltip>}>
                                <Button type="button" size='sm' variant='success' onMouseEnter={this.onUpdateEnter} onClick={this.onUpdateClick} style={{ position: 'absolute', top: '0', right: '1.5em', fontSize: '.65em', fontWeight: 'bold' }}>UPDATE</Button>
                            </OverlayTrigger>
                        </Col>
                        <Col className="col-padding">
                            <Form.Label>SERIES</Form.Label>
                            <Form.Control name="series" as="select" custom>
                                <option>EE Bond</option>
                                <option>I Bond</option>
                                <option>E Bond</option>
                                <option>Savings Note</option>
                            </Form.Control>
                        </Col>
                        <Col className="col-padding">
                            <Form.Label>DENOMINATION</Form.Label>
                            <Form.Control name="denomination" as="select" custom>
                                <option>$25</option>
                                <option>$50</option>
                                <option>$75</option>
                                <option>$100</option>
                                <option>$200</option>
                                <option>$500</option>
                                <option>$1,000</option>
                                <option>$5,000</option>
                                <option>$10,000</option>
                            </Form.Control>
                        </Col>
                        <Col className="col-padding">
                            <Form.Label size='sm'>SERIAL NUMBER</Form.Label>
                            <Form.Control
                                name="serialnumber"
                                type="text"
                                placeholder="Optional"
                            />
                        </Col>
                        <Col style={{ position: 'relative' }}>
                            <Form.Label>ISSUE DATE</Form.Label>
                            <InputGroup>
                                <Form.Control
                                    required
                                    name="iMonth"
                                    type="number"
                                    placeholder="Month"
                                    maxLength="2"
                                    min="1"
                                    max="12"
                                    defaultValue="11"
                                />
                                <InputGroup.Append>
                                    <InputGroup.Text id="inputGroupPrepend">/</InputGroup.Text>
                                </InputGroup.Append>
                                <Form.Control
                                    required
                                    name="iYear"
                                    type="number"
                                    placeholder="Year"
                                    maxLength="4"
                                    min="1940"
                                    max={this.state.year}
                                    style={{ marginLeft: '-1px' }}
                                    defaultValue="2005"
                                />
                            </InputGroup>
                            <Button type="submit" size='sm' style={{ position: 'absolute', top: '0', right: '.5em', fontSize: '.65em', fontWeight: 'bold' }}>SUBMIT</Button>
                        </Col>
                    </Form.Row>
                </Form >
                <div id="card-container" style={{ width: '100%', float: 'left', paddingTop: '1em' }}>
                    <div style={{ width: '100%', textAlign: 'right', marginBottom: '.25em' }}>
                        <CSVLink filename={'bonds_valued_from_' + this.state.month + '_' + this.state.year} data={this.state.exportResults.length > 0 ? this.state.exportResults : ''}>+ [Export]</CSVLink>
                    </div>
                    <Card style={this.state.showCards} className='card-one'>
                        <Card.Header style={{ fontSize: '14px', padding: '.50em', fontWeight: '500' }}>VALUE AS OF</Card.Header>
                        <Card.Body style={{ textAlign: 'center' }}>
                            <Card.Title>{this.state.currentBond ? this.formatDate(this.state.currentBond['vDate']) : null}</Card.Title>
                        </Card.Body>
                    </Card>
                    <Card style={this.state.showCards} className='card-two'>
                        <Card.Header style={{ fontSize: '14px', padding: '.50em', fontWeight: '500' }}>TOTAL COST</Card.Header>
                        <Card.Body style={{ textAlign: 'center' }}>
                            <Card.Title id="card-price">{this.state.totalPrice}</Card.Title>
                        </Card.Body>
                    </Card>
                    <Card style={this.state.showCards} className='card-three'>
                        <Card.Header style={{ fontSize: '14px', padding: '.50em', fontWeight: '500' }}>TOTAL INTEREST</Card.Header>
                        <Card.Body style={{ textAlign: 'center' }}>
                            <Card.Title id="card-interest">{this.state.totalInterest}</Card.Title>
                        </Card.Body>
                    </Card>
                    <Card style={this.state.showCards} className='card-four'>
                        <Card.Header style={{ fontSize: '14px', padding: '.50em', fontWeight: '500' }}>TOTAL VALUE</Card.Header>
                        <Card.Body style={{ textAlign: 'center' }}>
                            <Card.Title id="card-value">{this.state.totalValue}</Card.Title>
                        </Card.Body>
                    </Card>
                </div>
                <div style={{ width: '100%', float: 'left', marginTop: '1em' }}>
                    <Table striped bordered hover id="bond-table" hidden={this.state.hideTable} onChange={this.state.changeTable} style={{ width: '100%', margin: 'auto', marginBottom: '2em' }}>
                        <thead style={{ textAlign: "center", fontSize: "11px", textTransform: "uppercase" }}>
                            <tr>
                                <th>Serial Number</th>
                                <th>Series</th>
                                <th>Denomination</th>
                                <th>Issue Date</th>
                                <th>Next Accrual</th>
                                <th>Final Maturity</th>
                                <th>Issue Price</th>
                                <th>Interest</th>
                                <th>Value</th>
                            </tr>
                        </thead>
                    </Table>
                </div>
            </div >
        )
    }
}

export default BondPricingOne;
