import React, { Component } from "react";
import { Navbar } from "../../node_modules/react-bootstrap";
import { Form } from "../../node_modules/react-bootstrap";

class Bonds extends Component {
    constructor(props) {
        super(props);
        this.state = {
            label: 'Choose file'
        };

        this.setFilename = this.setFilename.bind(this);
    }

    handleFileRead(fileReader) {
        console.log(fileReader.result);
    };

    setFilename(e) {
        this.setState({ label: e.target.files[0].name })

        let fileReader = new FileReader();
        fileReader.onloadend = this.handleFileRead();
        fileReader.readAsText(e);
    }

    render() {
        return (
            <React.Fragment>
                <Navbar bg="dark" variant="dark">
                    <Navbar.Brand>Bond Pricing</Navbar.Brand>
                </Navbar>
                <Form className="form-csv">
                    <Form.File
                        id="custom-file"
                        label={this.state.label}
                        onChange={this.setFilename}
                        custom
                    />
                </Form>
            </React.Fragment>
        )
    }
}

export default Bonds;
