import React, { Component } from 'react';
import { Card } from 'react-bootstrap';
import { CSVLink } from "react-csv";
//import excel from '../images/excel.png';

class Instructions extends Component {
    state = {}

    render() {
        const exportData = [['Serial Number', 'Series', 'Denomination', 'Issue Date']]

        return (
            <React.Fragment>
                <div className="jumbotron-pages"></div>
                <div style={{ width: '70%', minWidth: '850px', margin: 'auto', marginTop: '2rem' }}>
                    <h5>PRICING BONDS BY SPREADSHEET</h5>
                    <Card style={{ width: '35%', float: 'left', padding: '1em' }}>
                        <Card.Title>STEP 1</Card.Title>
                        <Card.Text>
                            Download CSV Template: <CSVLink data={exportData}>[CSV Template]</CSVLink>
                        </Card.Text>
                    </Card>
                    <Card style={{ width: '60%', marginLeft: '5%', padding: '1em' }}>
                        <Card.Title>STEP 2</Card.Title>
                        <Card.Text>
                            For each of your bonds, insert a new row into the spreadsheet.
                        </Card.Text>
                    </Card>
                </div>
            </React.Fragment>
        )
    }
}

export default Instructions;
