import React, { Component } from 'react';
import { Navbar, Nav, NavDropdown, Jumbotron, ListGroup } from 'react-bootstrap';
import { BrowserRouter, Switch, Route } from 'react-router-dom';
import HomePage from './index';
import Instruction from './instructions';
import BondPricingOne from './bond_pricing_individual';
import BondPricingTwo from './bond_pricing_spreadsheet';
import BondExample from './bond_example';
import logo from '../images/logo.png';
import '../index.css'

class Navigation extends Component {
    constructor(props) {
        super(props);
        this.state = {
        };
    }

    render() {
        return (
            <Jumbotron className='font-test' style={{ color: '#343a40' }}>
                <BrowserRouter>
                    <Navbar>
                        <Navbar.Brand> <img
                            src={logo}
                            width="30"
                            height="30"
                            alt="SAVINGS BOND PRICING TOOL"
                        /></Navbar.Brand>
                        <Navbar.Brand></Navbar.Brand>
                        <Nav.Link href="/" style={{ fontSize: '14px', fontWeight: '500' }}>HOME</Nav.Link>
                        <Nav.Link href="/instructions" style={{ fontSize: '14px', fontWeight: '500' }}>INSTRUCTIONS</Nav.Link>
                        <Nav.Link href="/bond_example" style={{ fontSize: '14px', fontWeight: '500' }}>BOND EXAMPLE</Nav.Link>
                        <NavDropdown title="BOND PRICING" id="basic-nav-dropdown" style={{ fontSize: '14px', fontWeight: '500' }}>
                            <NavDropdown.Item href="/bond_pricing_individual" style={{ fontSize: '14px' }}>INDIVIDUAL</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item href="/bond_pricing_spreadsheet" style={{ fontSize: '14px' }}>SPREADSHEET</NavDropdown.Item>
                        </NavDropdown>
                    </Navbar>
                    <Switch>
                        <Route exact path='/' component={HomePage} />
                        <Route exact path='/instructions' component={Instruction} />
                        <Route exact path='/bond_example' component={BondExample} />
                        <Route exact path='/bond_pricing_individual' component={BondPricingOne} />
                        <Route exact path='/bond_pricing_spreadsheet' component={BondPricingTwo} />
                    </Switch>
                </BrowserRouter>
                <div style={{ marginLeft: '2rem' }}>
                    <h1 style={{ fontSize: '2em' }}>Quickly and easily price your savings bonds!</h1>
                    <h1 style={{ fontSize: '1em' }}>Value, aggregate, and export any set of I, E, EE Series bonds or Savings Notes.</h1>
                    <div style={{ width: '40%' }}>
                        <div style={{ margin: 'auto', marginTop: '2em', width: '60%' }}>
                            <p style={{ marginBottom: '.5rem', textTransform: 'uppercase' }}>Choose an option:</p>
                            <ListGroup>
                                <ListGroup.Item><a href="/bond_pricing_individual">Price bonds individually</a></ListGroup.Item>
                                <ListGroup.Item><a href="/bond_pricing_spreadsheet">Price bonds from spreadsheet</a></ListGroup.Item>
                            </ListGroup>
                        </div>
                    </div>
                </div>
            </Jumbotron>
        )
    }
}

export default Navigation;
